import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {
  searchQuery: string = '';
  @Output() searchByName: EventEmitter<string> = new EventEmitter<string>();

  onSearch(): void {
    this.searchByName.emit(this.searchQuery);
  }
}
<div class="container">
    <h2>Search for your cocktail</h2>
   <input type="text" placeholder="Search by name..." [(ngModel)]="searchQuery" (input)="onSearch()">

</div>
