import { NgFor } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { CocktailService } from 'src/app/cocktail-service.service';
import { Cocktail } from 'src/app/types/cocktailsType';
import { UserService } from 'src/app/user/user.service';

@Component({
  selector: 'app-current-cocktail',
  templateUrl: './current-cocktail.component.html',
  styleUrls: ['./current-cocktail.component.css']
})
export class CurrentCocktailComponent implements OnInit {
cocktail = {} as Cocktail;
isEditing: boolean = false;
isLiked: boolean = false;
  constructor(
    private cocktailService: CocktailService, 
    private activeRoute: ActivatedRoute,
    private router: Router,
    private userService: UserService
    ) {}

    ngOnInit(): void {
      this.activeRoute.params.subscribe((data) => {
        const valueId = 'cocktailId';
        const id = data[valueId];        
        this.cocktailService.getCocktail(id).subscribe(cocktail => {
         this.cocktail = cocktail;
        });
      });
    };
    onToggle: boolean = true;

// onEdit() {
//   // this.editCocktail()
// }
editCocktail(form: NgForm) {
  if (form.invalid) {return}
  form.value.id = this.cocktail._id;
  this.cocktailService.editCocktail(
    form.value.id,
    form.value.name,
    form.value.ingredients,
    form.value.instructions,
    form.value.imageURL).subscribe(cocktail => {
      this.cocktail = cocktail;
    }) 
    this.router.navigate([`/view/cocktails/${this.cocktail._id}`]);

}

// toggleLikedByUser(id: string): void {
//   this.cocktail._id = id;
//   this.cocktail.likedByUser = !this.cocktail.likedByUser;
//   this.cocktailService.updateCocktail(this.cocktail._id).subscribe(cocktail => {
//     this.cocktail = cocktail;
//     this.isLiked = !this.isLiked;
//     // this.router.navigate(['/view/dashboard']);

//   });
// }



deleteCocktail(id: String) {  
  const valueId: string = (this.cocktail._id).toString();

  this.cocktailService.deleteCocktail(valueId).subscribe(() => {
    this.router.navigate(['/view/cocktails']);
  })
}
}



<div *ngIf="onToggle; else editForm" class="container">
  <h1>{{cocktail.name}}</h1>

  <div class="cocktail-details">
    <img src="{{cocktail.imageURL}}" alt="Cocktail Image" class="cocktail-image">
    <!-- <p><span class="ingredient">Cocktail Name:</span> {{cocktail.name}}</p> -->
    <p><span class="ingredient">Ingredients:</span> {{cocktail.ingredients}}</p>
    <p><span class="ingredient">Instructions:</span><br>{{cocktail.instructions}}</p>
    <p>Likes: {{cocktail.likes || 0}}</p>

    <button (click)="onToggle = !onToggle">Edit</button>
    <!-- <button (click)="toggleLikedByUser(cocktail._id)" *ngIf="!isLiked">Like</button> -->
    <!-- <button (click)="toggleLikedByUser(cocktail._id)" *ngIf="isLiked">Unlike</button> -->

    <p><span class="ingredient">Browse for more inspiration:</span>
    <a routerLink="/view/cocktails">Back to Dashboard</a>
    </p>
  </div>
</div>
<ng-template #editForm>
  <div class="container">
  <h1>Edit Cocktail</h1>

  <form #form="ngForm" (ngSubmit)="editCocktail(form)">
    <div class="form-group">
      <label for="name">Cocktail Name:</label>
      <input type="text" id="name" name="name" [(ngModel)]="cocktail.name" 
      required
      ngModel
      >
    </div>
    <div class="form-group">
      <label for="ingredients">Ingredients:</label>
      <textarea id="ingredients" name="ingredients" [(ngModel)]="cocktail.ingredients" 
      required
      ngModel
      ></textarea>
    </div>
    <div class="form-group">
      <label for="instructions">Instructions:</label>
      <textarea id="instructions" name="instructions" [(ngModel)]="cocktail.instructions" 
      required
      ngModel
      ></textarea>
    </div>
    <div class="form-group">
      <label for="imageURL">imageURL:</label>
      <input type="text" id="imageURL" name="imageURL" [(ngModel)]="cocktail.imageURL"
      required
      ngModel
      #inputImageURL="ngModel"
      >
  </div>
    <button type="submit" (ngSubmit)="editCocktail(form)">Save Changes</button>
    <button type="submit" (click)="deleteCocktail(form.value?.id)">Delete Cocktail</button>
    <button (click)="onToggle = !onToggle">Cancel</button>

  </form>
</div>
</ng-template>
