import { Component, OnChanges, OnInit, SimpleChanges } from '@angular/core';
import { CocktailService } from 'src/app/cocktail-service.service';
import { Cocktail } from 'src/app/types/cocktailsType';

@Component({
  selector: 'app-cocktails-list',
  templateUrl: './cocktails-list.component.html',
  styleUrls: ['./cocktails-list.component.css']
})
export class CocktailsListComponent implements OnInit {
  cocktails: Cocktail[] | null = [];
constructor(private cocktailService: CocktailService,
  ) {}
isEmpty: boolean = false;
  ngOnInit(): void {
    this.cocktailService.getCocktails().subscribe((data) => {
this.cocktails = data;
return this.cocktails;
    });
    if (this.cocktails?.length === 0) {
      this.isEmpty = true;
    }
  }
}
<div  *ngIf="!!isEmpty" class="card-container">
  <div *ngFor="let cocktail of cocktails" class="card">
    <img src="{{cocktail.imageURL}}" 
    alt="{{cocktail.name}}">
    <div class="card-content">
        <h2>{{cocktail.name}}</h2>
        <p>Ingredients: {{cocktail.ingredients}}</p>
        <p>Likes: {{cocktail.likes || 0}}</p>
<a [routerLink]="['/cocktails', cocktail._id]">Details</a>
    </div>
    <div  *ngIf="!isEmpty" class="card-container">
        <h1>Sorry, there are no cocktails at this time.</h1>
        <p>Let's get our groove on and make some magic!</p>
        <a routerLink="/create-cocktail">Create a drink</a>

    </div> 
</div>

</div>
